class ApiAuth {
    constructor(options) {
        this._host = options.host;
        this._headers = options.headers;

    }

    _responseAnalysis(res) {
        debugger
        if (res.ok || res.status === 201) { return res.json(); }
        return Promise.reject(res.status)
    }

    // _request(data, body) {
    //     return fetch(`${this._host}${data.endpoint}`, {
    //         method: data.methodName,
    //         headers: this._headers,
    //         body: JSON.stringify({ 'password': body.password, 'email': body.email })
    //     }).then(res => this._responseAnalysis(res))
    // }

    _request(data, body) {
        const config = {
            method: data.methodName,
            headers: {
                headers: this._headers,
                ...(!!data.token && { Authorization: `Bearer ${data.token}` }),
            },
            ...(!!body && { body: JSON.stringify(body) }),
        };
        return fetch(`${this._host}${data.endpoint}`, {
            method: data.methodName,
            headers: this._headers,
            body: JSON.stringify({ 'password': body.password, 'email': data.email })
        }).then(res => this._responseAnalysis(res))
    }

    register(data, body) {
        return this._request(data, body)
    };

    authorization(data, body) {
        return this._request(data, body)
    }

    checkToken(data) {
        return this._request(data,)
    }

}


const apiAuth = new ApiAuth({
    host: 'https://auth.nomoreparties.co/',
    headers: { "Content-Type": "application/json" }
})

export default apiAuth;
