import React from 'react';
import apiAuth from '../utils/ApiAuth'

function Login() {
    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');

    function handleChange(e) {
        if (e.target.name === 'email') {
            setEmail(e.target.value)
        } else {
            setPassword(e.target.value)
        }
    };

    function handleSubmit() {
        e.preventDefault();
        debugger
        apiAuth.authorization({
            endpoint: 'signup',
            methodName: 'POST',
        }, email, password)
    }
    return (
        <form onSubmit={handleSubmit} className='authorization__form'>
            <h1 className='authorization__title'>Вход</h1>
            <fieldset className='authorization__fieldset'>
                <input className='authorization__input' onChange={handleChange} placeholder='Email'></input>
                <input className='authorization__input' onChange={handleChange} placeholder='Пароль'></input>
                <button type='submit' className='authorization__button'>Войти</button>
            </fieldset>
        </form>
    )
}

export default Login;
